#!/bin/bash\n\n# Quick test script for Amsterdam Street Art Map pricing\n# Usage: ./quick-pricing-test.sh [region]\n\nset -e\n\nAPI_URL=\"https://amsterdamstreetart-ow027wxkh-dpms-projects-8cd1083b.vercel.app/api\"\n\necho \"ğŸ¨ Amsterdam Street Art Map - Quick Pricing Test\"\necho \"===============================================\"\necho \"\"\n\nif [ \"$1\" != \"\" ]; then\n    # Test specific region\n    REGION=$1\n    echo \"Testing region: $REGION\"\n    \n    case $REGION in\n        \"centre\"|\"center\")\n            PRICE_ID=\"price_1RlrHzJ3urOr8HD7UDo4U0vY\"\n            ;;\n        \"noord\"|\"north\")\n            PRICE_ID=\"price_1RlrKYJ3urOr8HD7HzOpJ8bJ\"\n            ;;\n        \"east\")\n            PRICE_ID=\"price_1RbeqUJ3urOr8HD7ElBhh5rB\"\n            ;;\n        \"nieuw-west\"|\"new-west\")\n            PRICE_ID=\"price_1Rbf2kJ3urOr8HD7QTcbJLSo\"\n            ;;\n        \"south\"|\"zuid\")\n            PRICE_ID=\"price_1RbeqwJ3urOr8HD7Rf6mUldT\"\n            ;;\n        \"south-east\")\n            PRICE_ID=\"price_1Rbf8wJ3urOr8HD7gvLlK0aa\"\n            ;;\n        \"west\")\n            PRICE_ID=\"price_1Rbf23J3urOr8HD7gxyHwFW0\"\n            ;;\n        *)\n            echo \"âŒ Unknown region: $REGION\"\n            echo \"Available regions: centre, noord, east, nieuw-west, south, south-east, west\"\n            exit 1\n            ;;\n    esac\n    \n    echo \"Price ID: $PRICE_ID\"\n    echo \"Testing API...\"\n    \n    curl -s \"$API_URL/get-price?priceId=$PRICE_ID\" | jq '.' || echo \"(Response not JSON or jq not installed)\"\nelse\n    # Test all regions\n    echo \"Testing all regions...\"\n    echo \"\"\n    \n    declare -A REGIONS\n    REGIONS[\"centre\"]=\"price_1RlrHzJ3urOr8HD7UDo4U0vY\"\n    REGIONS[\"noord\"]=\"price_1RlrKYJ3urOr8HD7HzOpJ8bJ\"\n    REGIONS[\"east\"]=\"price_1RbeqUJ3urOr8HD7ElBhh5rB\"\n    REGIONS[\"nieuw-west\"]=\"price_1Rbf2kJ3urOr8HD7QTcbJLSo\"\n    REGIONS[\"south\"]=\"price_1RbeqwJ3urOr8HD7Rf6mUldT\"\n    REGIONS[\"south-east\"]=\"price_1Rbf8wJ3urOr8HD7gvLlK0aa\"\n    REGIONS[\"west\"]=\"price_1Rbf23J3urOr8HD7gxyHwFW0\"\n    \n    for region in \"${!REGIONS[@]}\"; do\n        price_id=\"${REGIONS[$region]}\"\n        printf \"%-12s: \" \"$region\"\n        \n        response=$(curl -s \"$API_URL/get-price?priceId=$price_id\")\n        \n        if echo \"$response\" | jq -e '.formattedPrice' > /dev/null 2>&1; then\n            price=$(echo \"$response\" | jq -r '.formattedPrice')\n            echo \"âœ… $price\"\n        else\n            echo \"âŒ Failed\"\n        fi\n    done\nfi\n\necho \"\"\necho \"ğŸ’¡ Tip: Use './quick-pricing-test.sh centre' to test a specific region\"\necho \"ğŸ“ API URL: $API_URL/get-price\"\n